mapper.select:
SELECT d.document_key documentKey,ddi.item_key itemKey, d.document_title documentTitle, d.document_age_limit documentAgeLimit, d.document_file_path documentFilePath,d.document_file_name documentFileName,d.document_file_time documentFileTime, d.created_at createdAt, d.created_way createdWay, d.created_by_key createdByKey, d.created_by_name createdByName, d.check_state checkState, d.check_report checkReport, d.checked_by_key checkedByKey, d.checked_by_name checkedByName,ddi.item_name itemName FROM kt_document d LEFT JOIN kt_document_category dc ON d.document_key=dc.document_key LEFT JOIN kt_data_dictionary_items ddi ON dc.item_key=ddi.item_key WHERE 1=1
AND d.created_way=#{createdWay}
AND d.check_state=#{checkState}
AND ddi.item_key=#{itemKey}
AND d.document_title LIKE CONCAT('%',#{documentTitle},'%')
AND d.document_age_limit=#{documentAgeLimit}
ORDER BY d.created_at DESC
